import React from "react";

import styles from './Card.module.css'
import { History, LocationState } from "history";

interface Props {
    name : String,
    nickname: String,
    website: String,
    id: Number,
    history: History<LocationState>
}

const Index: React.FC<Props> = ({
    name,
    nickname,
    website,
    id,
    history
}) => {

    const ILIKE2SHORTCHARACTERS = `https://eu.ui-avatars.com/api/name=${name}&color=000000`

    const seeMoreDetail: any = (id: number) => {
        console.log(id)
        history.push(`/${id}`)
    }

    return (
        <div className={styles.cardContainer}>
            <div className={styles.cardContent}>
                <img className={styles.profilePhoto} src={ILIKE2SHORTCHARACTERS}/>
                <div className={styles.name}>{ name }</div>
                <div className={styles.nickname}>{ nickname}</div>
                <a href="url" className={styles.website}>{ website }</a>
                <button className={styles.seeDetails} onClick={ () => seeMoreDetail(id)} ></button>
            </div>
        </div>
    )
}

export default Index;
